sys_script_email.script:
(function runMailScript(current, template, email, email_action, event) {

	var MONTHS = ["January","February","March","April","May","June","July","August","September","October","November","December"];
	var nextMonthIndex = 0;
	var currentMonth= current.month.getDisplayValue();
	var currentMonthIndex = MONTHS.indexOf(currentMonth);
	if(currentMonthIndex != 11){
		nextMonthIndex=currentMonthIndex+1;
	}
	template.print(MONTHS[nextMonthIndex+1]);

})(current, template, email, email_action, event);
