sys_script_include.script:
var PalmsnowActivityStream = Class.create();
PalmsnowActivityStream.prototype = {
  initialize: function (sys_id) {
    var tracked_fields = this.TRACK_FIELDS = gs.getProperty('x_aaro2_pnl_mgmt.question_history_tracked_fields')
    this.AUDIT_PROVIDER = new AuditHistoryProvider(sys_id, tracked_fields)
    this.SYS_HISTORY_PROVIDER = new SysHistoryProvider(sys_id, tracked_fields)
    this.QUESTION_HISTORY_PROVIDER = new QuestionHistoryProvider(sys_id, tracked_fields)
    this.JOURNAL_HISTORY_PROVIDER = new JournalHistoryProvider(sys_id)
    this.ATTACHMENT_HISTORY_PROVIDER = new AttachmentHistroyProvider(sys_id)
  },

  get: function () {
    var history = this.QUESTION_HISTORY_PROVIDER.get() ||
      this.SYS_HISTORY_PROVIDER.get() ||
      this.AUDIT_PROVIDER.get() || []

    var activityStream = history
      .concat(this.ATTACHMENT_HISTORY_PROVIDER.get())
      .concat(this.JOURNAL_HISTORY_PROVIDER.get())

    activityStream.sort(this._historySort)

    return activityStream;
  },
  _historySort: function (a, b) {
    var gdta = new GlideDateTime(a.update_time)
    var gdtb = new GlideDateTime(b.update_time)
    return gdtb.getNumericValue() - gdta.getNumericValue()
  },

  type: 'PalmsnowActivityStream'
};
