sys_script_include.script:
var AbstractHistoryProvider = Class.create();
AbstractHistoryProvider.prototype = {
  initialize: function (record_sys_id, tracked_fields) {
    this.RECORD_SYS_ID = record_sys_id
    this.TRACKED_FIELDS = tracked_fields
    this.HISTORY_TYPE = {
      FIELD: 'FIELD',
      ATTACHMENT: 'ATTACHMENT',
      JOURNAL: 'JOURNAL'
    }
  },



  _getUserBySysId: function (sys_id) {
    var userGr = new GlideRecord('sys_user');
    userGr.get(sys_id)
    return this._getUserObject(userGr)
  },
  _getUserById: function (sys_id) {
    var userGr = new GlideRecord('sys_user');
    userGr.get('user_name', sys_id)
    return this._getUserObject(userGr)
  },
  _getUserObject: function (gr) {
    var userObj = {
      id: gr.getUniqueValue(),
      name: gr.getValue('name'),
      avatar: gr.getValue('avatar') || '',
      email: gr.getValue('email')
    }
    return userObj;
  },


  type: 'AbstractHistoryProvider'
};
