sys_script_include.script:
var DeadlineActivity = Class.create();
DeadlineActivity.prototype = {
  initialize: function (sys_id) {
    this.RECORD_SYS_ID = sys_id
    this.HISTORY_SET_TABLE = 'sys_history_set',
      this.HISTORY_LINE_TABLE = 'sys_history_line'
    this.EMAIL_TABLE = 'sys_email'
  },
  get: function () {
    if(!this.RECORD_SYS_ID)return []
    var mails = []
    var mailGr = new GlideRecord(this.EMAIL_TABLE)
    mailGr.addQuery('instance', this.RECORD_SYS_ID)
    mailGr.orderByDesc('sys_created_on')
    mailGr.query()
    while (mailGr.next()) {
      mails.push(this._getMailObject(mailGr))
    }
    return mails
  },
  _getMailObject: function(gr){
    return {
      recipients: gr.getValue('recipients'),
      subject: gr.getValue('subject'),
      created_on: gr.getValue('sys_created_on'),
      body: gr.getValue('body')
    }
  },
  type: 'DeadlineActivity'
};
