sys_transform_entry.source_script:
answer = (function transformEntry(source) {

if(source.u_stepname != 'Saved') {
   return;
} else{
var rx = new RegExp(/^c\d{4}/);

if(rx.test(source.u_userid)) {
    var userGr = new GlideRecord('sys_user');
    userGr.addQuery('department.name', source.u_userid);
    userGr.addQuery('title', 'Store Manager');
    userGr.addQuery('active', true);
    userGr.query();
    if(userGr.next()) {
        return userGr.getUniqueValue();
    } 
}else if (source.u_userid){
    var userGr2 = new GlideRecord('sys_user');
    userGr2.addQuery('user_name',source.u_userid);
    userGr2.addQuery('active', true);
    userGr2.query();
    if(userGr2.next()) {
        return userGr2.getUniqueValue();
    }
}
}

})(source);
