sys_ui_action.script:
var dateStr = current.year +'-'+current.month + '-01';
	var accountingPeriodDate = new GlideDateTime(dateStr);
	accountingPeriodDate.addMonthsUTC(1);
	var u = new DeadlinesUtil(accountingPeriodDate);
	var deadlines = u.getDeadlines();
	gs.info(JSON.stringify(deadlines, null, 2))
	if(!current.getValue('early_access_start'))
		current.early_access_start = deadlines.sneakPeek.glideDateFrom;
	if(!current.getValue('early_access_end'))
		current.early_access_end = deadlines.sneakPeek.glideDateTo;
	if(!current.getValue('question_start'))
		current.question_start = deadlines.mulReview.glideDateFrom;
	if(!current.getValue('question_end'))
		current.question_end = deadlines.mulReview.glideDateTo;
// 	if(!current.getValue('review_start'))
// 		current.review_start = deadlines.accountingReview.glideDateFrom;
// 	if(!current.getValue('review_end'))
// 		current.review_end = deadlines.accountingReview.glideDateTo;

	if(!current.getValue('reassign_start'))
		current.reassign_start = deadlines.accountingCanReassignPeriod.glideDateFrom;
	if(!current.getValue('reassign_end'))
		current.reassign_end = deadlines.accountingCanReassignPeriod.glideDateTo;


	if(!current.getValue('answer_start'))
		current.answer_start = deadlines.accountingAnswerPeriod.glideDateFrom;
	if(!current.getValue('answer_end'))
		current.answer_end = deadlines.accountingAnswerPeriod.glideDateTo;
	current.update();
