sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	//  Property:: GetPNLStatementRows_endpoint
	var responseObj , restResponse;
	var endpoint = gs.getProperty('x_aaro2_pnl_mgmt.GetPNLStatementRows_endpoint');
	var deadline = new Utils().getDeadline(request.queryParams.FiscalYear, request.queryParams.AccountingPeriod, 'question');
	var inputQueryJson = JSON.stringify(request.queryParams);
	gs.info('inputQueryJson : '+inputQueryJson);
	gs.getUser().savePreference('x_aaro2_pnl_mgmt.statements', inputQueryJson);
	var timeValid = new Utils().timeValid(deadline.start, deadline.end);
	var askDeadline = {
		time_valid: timeValid,
		start: deadline.start,
		end: deadline.end
	};

	try { 
		var r = new sn_ws.RESTMessageV2();
		r.setHttpMethod('GET');
		r.setEndpoint(endpoint);
		r.setQueryParameter('FiscalYear',request.queryParams.FiscalYear);
		r.setQueryParameter('AccountingPeriod',request.queryParams.AccountingPeriod);
		r.setQueryParameter('ObjectId',request.queryParams.ObjectId);
		r.setQueryParameter('RefreshType',request.queryParams.RefreshType);
		r.setMIDServer(gs.getProperty('mid.server.rba_default'));
		r.setRequestHeader('Content-Type', 'application/json');
		r.setRequestHeader('Accept','application/json');
		r.setHttpTimeout(5000);                            
		restResponse = r.execute();
		restResponse.waitForResponse(10);   
		responseObj = new global.RESTResponseUtils(restResponse);
		responseObj.body = JSON.parse(responseObj.body);
	}
	catch(ex) {
		responseObj = new global.RESTResponseUtils(restResponse);
		responseObj.setBody(ex.toString());
		responseObj.setErrorMessage("There was an error calling the webservice.");
		responseObj.setStatusCode(503);
		responseObj.setHaveError(true);
		gs.error(responseObj);
	}


	response.setContentType("application/json");
	var stream = response.getStreamWriter();
	stream.writeString(JSON.stringify({
		rows: responseObj.body.Rows,
		askDeadline: askDeadline
	}));


})(request, response);
