sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	// x_aaro2_pnl_mgmt.GetPNLSnapshot_endpoint
	var responseObj , restResponse , endpoint;
	var body = request.body.dataString;
	var data = JSON.parse(body);

	function getExportID(){
		var exportGr = new GlideRecord('x_aaro2_pnl_mgmt_palmsnow_export');
		exportGr.initialize();
		var exportID = exportGr.insert();
		return {
			table: exportGr.getTableName(),
			sys_id: exportID
		};
	}

	try { 
		endpoint = gs.getProperty('x_aaro2_pnl_mgmt.export_excel');
		var r = new sn_ws.RESTMessageV2();
		r.setHttpMethod('POST');
		r.setEndpoint(endpoint);
		r.setMIDServer(gs.getProperty('mid.server.rba_default'));
		r.setRequestHeader('Content-Type', 'application/json');
		r.setHttpTimeout(10000);              
		r.setRequestBody(body);
		var exportRec = getExportID();
		r.saveResponseBodyAsAttachment(exportRec.table,exportRec.sys_id,data.excelName+'.xlsx');
		restResponse = r.execute();
		responseObj = {link:'sys_attachment.do?sys_id='+restResponse.getResponseAttachmentSysid()};	
	}
	catch(ex) {
		responseObj = new global.RESTResponseUtils(restResponse);
		responseObj.setBody(ex.toString());
		responseObj.setErrorMessage("There was an error calling the webservice.");
		responseObj.setStatusCode(503);
		responseObj.setHaveError(true);
		gs.error(responseObj);
	}
	return responseObj;

})(request, response);
