sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var responseObj = {}

	var userSysId = gs.getUserID();
	gs.info('PNL API:: ' + userSysId)
	var userGr = new GlideRecord('sys_user');
	userGr.get(userSysId);

	var locationSysId = userGr.getValue('company');

	var companyGr = new GlideRecord('core_company');
	companyGr.get(locationSysId);

	var division = companyGr.u_division.getRefRecord();
	var region = companyGr.u_region.getRefRecord();
	var company = {
		name: companyGr.getValue('u_name_dept'),
		sys_id: companyGr.getUniqueValue(),
		city: companyGr.getValue('city'),
		state: companyGr.getValue('state'),
		division: {
			name: division.getValue('u_name_dept'),
			sys_id: division.getUniqueValue()
		},
		region:{
			name: region.getValue('u_name_dept'),
			sys_id: region.getUniqueValue()
		} 
	}
	var userPic = userGr.photo.getDisplayValue()
	var photoLnk = userPic? ('https:/'+gs.getProperty("instance_name")+'.service-now.com/'+userPic): '';
	responseObj = {
		user:{
			name: userGr.getValue('name'),
			id: userGr.getValue('user_name'),
			sys_id: userGr.getUniqueValue(),
			title: userGr.getValue('title'),
			email: userGr.getValue('email'),
			photo: photoLnk
		},
		company: company
	}
	return responseObj;
})(request, response);
