sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var responseObj , restResponse , endpoint, access={} ;

	try { 
		endpoint = gs.getProperty('x_aaro2_pnl_mgmt.GetPNLStatementDatailRows_endpoint') +"FiscalYear="+ request.queryParams.FiscalYear +"&AccountingPeriod="+request.queryParams.AccountingPeriod+'&Company='+request.queryParams.Company+'&Account='+request.queryParams.Account+"&ObjectId=" + request.queryParams.ObjectId +"&RefreshType="+request.queryParams.RefreshType;
		var r = new sn_ws.RESTMessageV2();
		r.setHttpMethod('GET');
		r.setEndpoint(endpoint);
		r.setMIDServer(gs.getProperty('mid.server.rba_default'));
		r.setRequestHeader('Content-Type', 'application/json');
		r.setRequestHeader('Accept','application/json');
		r.setHttpTimeout(5000);                            
		restResponse = r.execute();
		restResponse.waitForResponse(10);   
		responseObj = new global.RESTResponseUtils(restResponse);
		var roleAccessGr = new GlideRecord('x_aaro2_pnl_mgmt_pnl_role_access');
		if(roleAccessGr.get('ents_role_id', request.queryParams.Role.toString())){
			access = {
				ents_role_id: roleAccessGr.getValue('ents_role_id'),
				ents_role_name: roleAccessGr.getValue('ents_role_name'),
				can_ask: +roleAccessGr.getValue('can_ask'),
				can_answer: +roleAccessGr.getValue('can_answer'),
				over_1k: +roleAccessGr.getValue('over_1k'),
				manager_review: +roleAccessGr.getValue('manager_review'),
				supervisor_review: +roleAccessGr.getValue('supervisor_review')
			};
		}
		responseObj = JSON.parse(responseObj.body);
		responseObj.access = access;
	}
	catch(ex) {
		responseObj = new global.RESTResponseUtils(restResponse);
		responseObj.setBody(ex.toString());
		responseObj.setErrorMessage("There was an error calling the webservice.");
		responseObj.setStatusCode(503);
		responseObj.setHaveError(true);
		gs.error(responseObj);
	}

	return responseObj;


})(request, response);
