sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var responseObj;
	var endpoint = gs.getProperty('x_aaro2_pnl_mgmt.GetPNLStatementDetailRowReferenceData');
	var year = request.queryParams.FiscalYear;
	var period = request.queryParams.AccountingPeriod;
	var company = request.queryParams.Company;
	var objId = request.queryParams.ObjectId;
	var reference = request.queryParams.Reference;

	try { 

		var r = new sn_ws.RESTMessageV2();
		r.setHttpMethod('GET');
		r.setEndpoint(endpoint);
		r.setQueryParameter('FiscalYear', year);
		r.setQueryParameter('AccountingPeriod', period);
		r.setQueryParameter('Company', company);
		r.setQueryParameter('ObjectId', objId);
		r.setQueryParameter('Reference', reference);
		r.setMIDServer(gs.getProperty('mid.server.rba_default'));
		r.setRequestHeader('Content-Type', 'application/json');
		r.setRequestHeader('Accept','application/json');
		r.setHttpTimeout(5000);                            
		restResponse = r.execute();
		restResponse.waitForResponse(10);   
		responseObj = new global.RESTResponseUtils(restResponse);

	}catch(ex) {
		responseObj = new global.RESTResponseUtils(restResponse);
		responseObj.setBody(ex.toString());
		responseObj.setErrorMessage("There was an error calling the webservice.");
		responseObj.setStatusCode(503);
		responseObj.setHaveError(true);
		gs.error(responseObj);
	}

	response.setContentType("application/json");
	var stream = response.getStreamWriter();
	stream.writeString(responseObj.body);

})(request, response);
