sys_ws_operation.operation_script:
(function process( /*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var deadlineData = new Utils().getCurrentPeriodDeadline('answer');
	var befeoreDeadline = deadlineData.beforeDeadline;
	var curYear = deadlineData.curYear;
	var curMonth = deadlineData.curMonth;
	var curUser = 'lcortes'; //gs.getUserName();  TEST DATA
	var userSysID = 'ec77b947134b16001a84bf304244b07f'; //gs.getUserID(); TEST DATA

	var tempAcc = new GlideRecord('x_aaro2_pnl_mgmt_temporary_access');
	if(tempAcc.get('user', userSysID)) {
		gs.info('utils tempAcc.getValue - ' + tempAcc.getValue('can_ask'));
		if(tempAcc.getValue('can_ask')) {
			curUser = 'nefiveas';
		}
	}

	var responseObj, restResponse, endpoint;
	gs.info('PNL MANAGEMENT: ' + JSON.stringify(request.queryParams));

	try {
		endpoint = gs.getProperty('x_aaro2_pnl_mgmt.GetPNLUserDataSet_endpoint') + "UserLogin=" + curUser;//gs.getUserName(); 'nefiveas'replace to  'srkincan'
		gs.info(endpoint);
		var r = new sn_ws.RESTMessageV2();
		r.setHttpMethod('GET');
		r.setEndpoint(endpoint);
		r.setMIDServer(gs.getProperty('mid.server.rba_default'));
		r.setRequestHeader('Content-Type', 'application/json');
		r.setRequestHeader('Accept', 'application/json');
		r.setHttpTimeout(5000);
		restResponse = r.execute();
		restResponse.waitForResponse(10);
		responseObj = new global.RESTResponseUtils(restResponse);
	} catch (ex) {
		responseObj = new global.RESTResponseUtils(restResponse);
		responseObj.setBody(ex.toString());
		responseObj.setErrorMessage("There was an error calling the webservice.");
		responseObj.setStatusCode(503);
		responseObj.setHaveError(true);
		gs.error(responseObj);
	}

	//test data START

	var body = JSON.parse(responseObj.body);
	var periods = body.Periods;
	periods.push({
		"FiscalYear": 2021,
		"AccountingPeriod": 1
	});

	body.Periods = periods;
	responseObj.body = JSON.stringify(body);

	//test data END

	//check if User has Pre-release Access START

	var preRelAcc = new GlideRecord('x_aaro2_pnl_mgmt_pre_release_access');
	if(!preRelAcc.get('user', userSysID) && befeoreDeadline) {
		var preBody = JSON.parse(responseObj.body);
		var prePeriods = preBody.Periods;
		var filtered = prePeriods.filter(function(period){
			return !(period.FiscalYear == curYear && period.AccountingPeriod == curMonth);
		});
		body.Periods = filtered;
		responseObj.body = JSON.stringify(body);

	}

	//check if User has Pre-release Access END

	return responseObj;

})(request, response);
